mapper.select:
/* login.findUserId */

SELECT
user_id as userId
FROM
kcgbd.t_user

WHERE 1=1

	AND name = #{userNm}


	AND mbl_telno = #{mblTelno}


	AND jncmp_Ymd = #{jncmpYmd}


	AND email = #{emlAddr}

/* login.findUserPw */

SELECT
user_pw as userPw
FROM
kcgbd.t_user

WHERE 1=1

	AND name = #{userNm}


	AND mbl_telno = #{mblTelno}


	AND jncmp_Ymd = #{jncmpYmd}


	AND email = #{emlAddr}


	AND user_id = #{userId}

mapper.insert:
/*
login.insertUserAuthInfo */
INSERT INTO kcgbd.t_user (
auth_cd
,user_id
,iam_yn
,reg_dt
,status_cd
,name
,jikgub_cd
,jikgub_nm
,dept
,tdept_nm
,email
,del_yn
) VALUES (
#{auth_cd}
,#{user_id}
,#{iam_yn}
,now()
,#{status_cd}
,#{name}
,#{jikgub_cd}
,#{jikgub_nm}
,#{dept}
,#{tdept_nm}
,#{email}
,'N'
)

mapper.update:
/*
login.updateUserAuthInfo */
UPDATE kcgbd.t_user SET
auth_cd = #{auth_cd}
,status_cd = #{status_cd}
,name = #{name}
,jikgub_cd = #{jikgub_cd}
,jikgub_nm = #{jikgub_nm}
,dept = #{dept}
,tdept_nm = #{tdept_nm}
,email
= #{email}
WHERE 1=1
AND user_id = #{user_id}
AND iam_yn = #{iam_yn}
