mapper.select:
SELECT
	comm_no AS comm_no,
	comm_nm AS comm_nm,
	group_comm_no AS gr_comm_no
FROM kcgbd.e_common
WHERE 1=1

  AND group_comm_no = #{gr_comm_no}


  OR group_comm_no = #{gr_comm_no1}


  OR group_comm_no = #{gr_comm_no2}


  OR group_comm_no = #{gr_comm_no3}


  OR group_comm_no = #{gr_comm_no4}

mapper.update:
UPDATE 
	kcgbd.e_common 
SET
	group_comm_nm = #{gr_comm_nm}
WHERE 1=1
  AND group_comm_no = #{gr_comm_no}
