mapper.select:
/* system.promion_mng.getlist */ select b.cust_nm , a.prod_ds_sn , a.cust_mbl_telno , to_char(a.wrt_dt,'yyyy-mm-dd') wrt_dt , b.cust_eml_addr , a1.prod_ty_cd , (select code_nm from kcgbd.tb_codemst where code_cl = '121' and code_no = a1.prod_ty_cd ) prod_ty_cd_nm , a.prod_nm , a.suggest_amt , d.pic_nm , '1' simpl_ty_cd /* 간편타입코드 : 1.정상, 0.간편 */ from (select prod_ds_sn , wrt_dt , prod_cd , prod_nm , tot_dpst_amt suggest_amt , cust_mbl_telno from kcgbd.tb_proddsgnsavgpl union select prod_ds_sn , wrt_dt , prod_cd , prod_nm , tot_dpst_amt suggest_amt , cust_mbl_telno from kcgbd.tb_proddsgnacmlpl union select prod_ds_sn , wrt_dt , prod_cd , prod_nm , tot_dpst_amt suggest_amt , cust_mbl_telno from kcgbd.tb_proddsgndpstpl union select prod_ds_sn , wrt_dt , prod_cd , prod_nm , tot_pay_amt suggest_amt , cust_mbl_telno from kcgbd.tb_proddsgnloanpl ) a , kcgbd.tb_prodmst a1 , kcgbd.tb_custmst b , kcgbd.tb_custpicrel c , kcgbd.tb_picmst d where a.prod_cd = a1.prod_cd and a.cust_mbl_telno = b.cust_mbl_telno and a.cust_mbl_telno = c.cust_mbl_telno and c.pic_mbl_telno = d.pic_mbl_telno
and b.cust_nm like concat('%',#{cust_nm},'%')
and a.prod_nm like concat('%',#{prod_nm},'%')
and d.pic_nm like concat('%',#{pic_nm},'%')
and to_char(a.wrt_dt,'yyyy-mm-dd') = #{wrt_dt}
order by a.wrt_dt desc, a.prod_ds_sn desc

mapper.insert:
/* system.promion_mng.insertDsgnSavgpl */
	insert 
	  into kcgbd.tb_proddsgnsavgpl (
	  	   prod_ds_sn
	  	 , int_cty_cd
	  	 , prod_nm
	  	 , pay_ty_cd
	  	 , circle_acml_amt
	  	 , goal_prd
	  	 , aply_rate
	  	 , int_tax_ty_cd
	  	 , wrt_dt
	  	 , tot_dpst_amt
	  	 , tot_dpst_int
	  	 , int_tax_amt
	  	 , atx_rcve_amt
	  	 , cust_mbl_telno
	  	 , prod_cd
	  	 )
	values (
	       (select coalesce(max(prod_ds_sn), 0) + 1 from kcgbd.tb_proddsgnsavgpl)
	  	 , #{int_cty_cd}
	  	 , #{prod_nm}
	  	 , #{pay_ty_cd}
	  	 , #{circle_acml_amt}
	  	 , #{goal_prd}
	  	 , cast(#{aply_rate} as numeric(5,2))
	  	 , #{int_tax_ty_cd}
	  	 , now()
	  	 , #{tot_dpst_amt}
	  	 , #{tot_dpst_int}
	  	 , #{int_tax_amt}
	  	 , #{atx_rcve_amt}
	  	 , #{cust_mbl_telno}
	  	 , #{prod_cd}
	     )

mapper.update:
/* system.promion_mng.updateDsgnSavgpl */
	update kcgbd.tb_proddsgnsavgpl
	   set int_cty_cd = #{int_cty_cd}
	     , prod_nm = #{prod_nm}
	     , pay_ty_cd = #{pay_ty_cd}
	     , circle_acml_amt = cast(#{circle_acml_amt} as numeric(15))
	     , goal_prd = cast(#{goal_prd} as numeric(5))
	     , aply_rate = cast(#{aply_rate} as numeric(5,2))
	     , int_tax_ty_cd = #{int_tax_ty_cd}
	     , wrt_dt = now()
	     , tot_dpst_amt = cast(#{tot_dpst_amt} as numeric(15))
	     , tot_dpst_int = cast(#{tot_dpst_int} as numeric(15))
	     , int_tax_amt = cast(#{int_tax_amt} as numeric(15))
	     , atx_rcve_amt = cast(#{atx_rcve_amt} as numeric(15))
	     , cust_mbl_telno = #{cust_mbl_telno}
	     , prod_cd = #{prod_cd}
	 where prod_ds_sn = cast(#{prod_ds_sn} as numeric(5))

