mapper.select:
/* system.team2.Cal.getProdList */ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_save where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

SELECT ep.prod_no, ep.prod_nm, ec_type.comm_nm AS prod_type, ec_target.comm_nm AS sub_tg, ep.pay_period, ec_cycle.comm_nm AS pay_cycle, ep.price_max, ep.price_min, ec_stat.comm_nm AS sale_stat, TO_CHAR(ep.sale_beg_dt, 'YYYY-MM-DD') AS sale_beg_dt, TO_CHAR(ep.sale_end_dt, 'YYYY-MM-DD') AS sale_end_dt, ep.promtn_yn, ec_interest.comm_nm AS interest_ta, ep.air_max, ep.air_min, TO_CHAR(ep.air_beg_dt, 'YYYY-MM-DD') AS air_beg_dt, TO_CHAR(ep.air_end_dt, 'YYYY-MM-DD') AS air_end_dt FROM kcgbd.e_product_save ep LEFT JOIN kcgbd.e_common ec_type ON ep.prod_type = ec_type.comm_no AND ec_type.group_comm_no = 4 LEFT JOIN kcgbd.e_common ec_target ON ep.sub_tg = ec_target.comm_no AND ec_target.group_comm_no = 6 LEFT JOIN kcgbd.e_common ec_cycle ON ep.pay_cycle = ec_cycle.comm_no AND ec_cycle.group_comm_no = 5 LEFT JOIN kcgbd.e_common ec_stat ON ep.sale_stat = ec_stat.comm_no AND ec_stat.group_comm_no = 8 LEFT JOIN kcgbd.e_common ec_interest ON ep.interest_ta = ec_interest.comm_no AND ec_interest.group_comm_no = 7
where ep.promtn_yn = #{promtn_yn}
and ep.prod_nm like CONCAT('%',#{prod_nm},'%')
and ep.prod_type = #{prod_type}
and ep.sub_tg = #{sub_tg}
AND CAST(#{from_date} AS DATE) BETWEEN ep.sale_beg_dt AND ep.sale_end_dt
and ep.sale_stat = #{sale_stat}
and ep.price_max < CAST(#{price_max} AS INTEGER)-1
and ep.price_min > CAST(#{price_min} AS INTEGER)+1
UNION ALL SELECT el.prod_no, el.prod_nm, ec_type.comm_nm AS prod_type, ec_target.comm_nm AS sub_tg, el.pay_period, ec_cycle.comm_nm AS pay_cycle, el.price_max, el.price_min, ec_stat.comm_nm AS sale_stat, TO_CHAR(el.sale_beg_dt, 'YYYY-MM-DD') AS sale_beg_dt, TO_CHAR(el.sale_end_dt, 'YYYY-MM-DD') AS sale_end_dt, el.promtn_yn, ec_interest.comm_nm AS interest_ta, el.air_max, el.air_min, TO_CHAR(el.air_beg_dt, 'YYYY-MM-DD') AS air_beg_dt, TO_CHAR(el.air_end_dt, 'YYYY-MM-DD') AS air_end_dt FROM kcgbd.e_product_loan el LEFT JOIN kcgbd.e_common ec_type ON el.prod_type = ec_type.comm_no AND ec_type.group_comm_no = 4 LEFT JOIN kcgbd.e_common ec_target ON el.sub_tg = ec_target.comm_no AND ec_target.group_comm_no = 6 LEFT JOIN kcgbd.e_common ec_cycle ON el.pay_cycle = ec_cycle.comm_no AND ec_cycle.group_comm_no = 5 LEFT JOIN kcgbd.e_common ec_stat ON el.sale_stat = ec_stat.comm_no AND ec_stat.group_comm_no = 8 LEFT JOIN kcgbd.e_common ec_interest ON el.interest_ta = ec_interest.comm_no AND ec_interest.group_comm_no = 7
where el.promtn_yn = #{promtn_yn}
and el.prod_nm like CONCAT('%',#{prod_nm},'%')
and el.prod_type = #{prod_type}
and el.sub_tg = #{sub_tg}
AND CAST(#{from_date} AS DATE) BETWEEN el.sale_beg_dt AND el.sale_end_dt
and el.sale_stat = #{sale_stat}
and el.price_max < CAST(#{price_max} AS INTEGER)-1
and el.price_min > CAST(#{price_min} AS INTEGER)+1
UNION ALL SELECT ea.prod_no, ea.prod_nm, ec_type.comm_nm AS prod_type, ec_target.comm_nm AS sub_tg, ea.pay_period, ec_cycle.comm_nm AS pay_cycle, ea.price_max, ea.price_min, ec_stat.comm_nm AS sale_stat, TO_CHAR(ea.sale_beg_dt, 'YYYY-MM-DD') AS sale_beg_dt, TO_CHAR(ea.sale_end_dt, 'YYYY-MM-DD') AS sale_end_dt, ea.promtn_yn, ec_interest.comm_nm AS interest_ta, ea.air_max, ea.air_min, TO_CHAR(ea.air_beg_dt, 'YYYY-MM-DD') AS air_beg_dt, TO_CHAR(ea.air_end_dt, 'YYYY-MM-DD') AS air_end_dt FROM kcgbd.e_product_ac ea LEFT JOIN kcgbd.e_common ec_type ON ea.prod_type = ec_type.comm_no AND ec_type.group_comm_no = 4 LEFT JOIN kcgbd.e_common ec_target ON ea.sub_tg = ec_target.comm_no AND ec_target.group_comm_no = 6 LEFT JOIN kcgbd.e_common ec_cycle ON ea.pay_cycle = ec_cycle.comm_no AND ec_cycle.group_comm_no = 5 LEFT JOIN kcgbd.e_common ec_stat ON ea.sale_stat = ec_stat.comm_no AND ec_stat.group_comm_no = 8 LEFT JOIN kcgbd.e_common ec_interest ON ea.interest_ta = ec_interest.comm_no AND ec_interest.group_comm_no = 7
where ea.promtn_yn = #{promtn_yn}
and ea.prod_nm like CONCAT('%',#{prod_nm},'%')
and ea.prod_type = #{prod_type}
and ea.sub_tg = #{sub_tg}
AND CAST(#{from_date} AS DATE) BETWEEN ea.sale_beg_dt AND ea.sale_end_dt
and ea.sale_stat = #{sale_stat}
and ea.price_max < CAST(#{price_max} AS INTEGER)-1
and ea.price_min > CAST(#{price_min} AS INTEGER)+1
UNION ALL SELECT ed.prod_no, ed.prod_nm, ec_type.comm_nm AS prod_type, ec_target.comm_nm AS sub_tg, ed.pay_period, ec_cycle.comm_nm AS pay_cycle, ed.price_max, ed.price_min, ec_stat.comm_nm AS sale_stat, TO_CHAR(ed.sale_beg_dt, 'YYYY-MM-DD') AS sale_beg_dt, TO_CHAR(ed.sale_end_dt, 'YYYY-MM-DD') AS sale_end_dt, ed.promtn_yn, ec_interest.comm_nm AS interest_ta, ed.air_max, ed.air_min, TO_CHAR(ed.air_beg_dt, 'YYYY-MM-DD') AS air_beg_dt, TO_CHAR(ed.air_end_dt, 'YYYY-MM-DD') AS air_end_dt FROM kcgbd.e_product_dp ed LEFT JOIN kcgbd.e_common ec_type ON ed.prod_type = ec_type.comm_no AND ec_type.group_comm_no = 4 LEFT JOIN kcgbd.e_common ec_target ON ed.sub_tg = ec_target.comm_no AND ec_target.group_comm_no = 6 LEFT JOIN kcgbd.e_common ec_cycle ON ed.pay_cycle = ec_cycle.comm_no AND ec_cycle.group_comm_no = 5 LEFT JOIN kcgbd.e_common ec_stat ON ed.sale_stat = ec_stat.comm_no AND ec_stat.group_comm_no = 8 LEFT JOIN kcgbd.e_common ec_interest ON ed.interest_ta = ec_interest.comm_no AND ec_interest.group_comm_no = 7
where ed.promtn_yn = #{promtn_yn}
and ed.prod_nm like CONCAT('%',#{prod_nm},'%')
and ed.prod_type = #{prod_type}
and ed.sub_tg = #{sub_tg}
AND CAST(#{from_date} AS DATE) BETWEEN ed.sale_beg_dt AND ed.sale_end_dt
and ed.sale_stat = #{sale_stat}
and ed.price_max < CAST(#{price_max} AS INTEGER)-1
and ed.price_min > CAST(#{price_min} AS INTEGER)+1

mapper.update:
update kcgbd.e_product_save set
sale_stat = #{sale_stat}
where prod_no = #{prod_no}

update kcgbd.e_product_loan set
sale_stat = #{sale_stat}
where prod_no = #{prod_no}

update kcgbd.e_product_dp set
sale_stat = #{sale_stat}
where prod_no = #{prod_no}

update kcgbd.e_product_ac set
sale_stat = #{sale_stat}
where prod_no = #{prod_no}
