mapper.select:
/* system.team2.Cal.getDp*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_dp where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.Cal.getLoan*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_loan where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.Cal.getAc*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_ac where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.promion_mng.getlist */ select a.tot_dpst_amt as suggest_amt, b.cust_nm, b.cust_eml_addr, s.prod_nm as prod_nm, l.prod_nm as loan_prod_nm, d.prod_nm as dp_prod_nm, c.prod_nm as ac_prod_nm, to_char(a.wrt_dt,'yyyy-mm-dd') as wrt_dt, cm.comm_nm as prod_type, e.emp_nm, a.plan_no, a.cust_sn, a.init_yn, '1' as simpl_ty_cd from kcgbd.e_planlist a left join kcgbd.e_customer b on a.cust_sn = b.cust_sn left join kcgbd.e_product_save s on a.prod_no = s.prod_no left join kcgbd.e_product_loan l on a.loan_prod_no = l.prod_no left join kcgbd.e_product_dp d on a.dp_prod_no = d.prod_no left join kcgbd.e_product_ac c on a.ac_prod_no = c.prod_no left join kcgbd.e_common cm on a.prod_type = cm.comm_no left join kcgbd.e_employee e on b.emp_no = e.emp_no where a.init_yn = 'N'
and b.cust_nm like concat('%', #{cust_nm}, '%')
and ( s.prod_nm like concat('%', #{prod_nm}, '%') or l.prod_nm like concat('%', #{prod_nm}, '%') or d.prod_nm like concat('%', #{prod_nm}, '%') or c.prod_nm like concat('%', #{prod_nm}, '%') )
and e.emp_nm like concat('%', #{emp_nm}, '%')
and to_char(a.wrt_dt, 'yyyy-mm-dd') = #{wrt_dt}
order by a.wrt_dt desc

mapper.update:
/* system.team2.promion_mng.updateDsgnSavgpl */
	update kcgbd.e_planlist
	   set circle_acml_amt = cast(#{circle_acml_amt} as numeric(8))
	  	 , goal_prd = cast(#{goal_prd} as numeric(8))
	  	 , aply_rate = cast(#{aply_rate} as numeric(5,2))
	  	 , interest_ta = #{interest_ta}
	  	 , wrt_dt = TO_DATE(#{wrt_dt}, 'yyyy-mm-dd')
	  	 , tot_dpst_amt = #{tot_dpst_amt}
	  	 , tot_dpst_int = #{tot_dpst_int}
	  	 , int_tax_amt = #{int_tax_amt}
	  	 , atx_rcve_amt = #{atx_rcve_amt}
	  	 , cust_sn = cast(#{cust_sn} as numeric(5))
	  	 , prod_no = #{prod_no}
	  	 , prod_type = #{prod_type}
	 where plan_no = cast(#{plan_no} as numeric(5))

mapper.insert:
/* system.team2.promion_mng.insertDsgnAcmlpl */
insert
	into kcgbd.e_planlist (
	  	  goal_amt
	  	 , goal_prd
	  	 , aply_rate
	  	 , interest_ta
	  	 , wrt_dt
	  	 , tot_dpst_amt
	  	 , tot_dpst_int
	  	 , int_tax_amt
	  	 , atx_rcve_amt
	  	 , cust_sn
	  	 , ac_prod_no
	  	 , prod_type
	  	 )
	values (
	  	  #{goal_amt}
	  	 , #{goal_prd}
	  	 , cast(#{aply_rate} as numeric(5,2))
	  	 , #{interest_ta}
	  	 , now()
	  	 , #{tot_dpst_amt}
	  	 , #{tot_dpst_int}
	  	 , #{int_tax_amt}
	  	 , #{atx_rcve_amt}
	  	 , cast(#{cust_sn} as numeric(5))
	  	 , #{prod_no}
	  	 , #{prod_type}
	     )

