mapper.select:
/* system.team2.Cal.getDp*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_dp where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.Cal.getDp*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_loan where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.Cal.getAc*/ select prod_no , prod_nm , prod_type , sub_tg from kcgbd.e_product_ac where 1=1
and cast(prod_no as text) like concat('%',#{prod_no},'%')
and prod_nm like concat('%',#{prod_nm},'%')
and prod_type = #{prod_type}

/* system.team2.promion_mng.getlist */ select tot_dpst_amt suggest_amt , (select cust_nm from kcgbd.e_customer where cust_sn = a.cust_sn) cust_nm , (select cust_eml_addr from kcgbd.e_customer where cust_sn = a.cust_sn) cust_eml_addr , (select prod_nm from kcgbd.e_product_save where prod_no = a.prod_no) prod_nm , (select prod_nm from kcgbd.e_product_loan where prod_no = a.loan_prod_no) prod_nm , (select prod_nm from kcgbd.e_product_dp where prod_no = a.dp_prod_no) prod_nm , (select prod_nm from kcgbd.e_product_ac where prod_no = a.ac_prod_no) prod_nm , to_char(a.wrt_dt,'yyyy-mm-dd') wrt_dt , (select comm_nm from kcgbd.e_common where comm_no = a.prod_type) prod_type , (select emp_nm from kcgbd.e_employee where emp_no = b.emp_no) emp_nm , plan_no , a.cust_sn , '1' simpl_ty_cd from kcgbd.e_planlist a left join kcgbd.e_customer b on a.cust_sn = b.cust_sn where 1=1
and (select cust_nm from kcgbd.e_customer where cust_sn = a.cust_sn) like concat('%',#{cust_nm},'%')
and (select prod_nm from kcgbd.e_product_save where prod_no = a.prod_no) like concat('%',#{prod_nm},'%')
and (select emp_nm from kcgbd.e_employee where emp_no = b.emp_no) like concat('%',#{emp_nm},'%')
and to_char(a.wrt_dt,'yyyy-mm-dd') = #{wrt_dt}
order by wrt_dt desc

mapper.insert:
/* system.team2.promion_mng.insertDsgnSavgpl */
	insert 
	  into kcgbd.e_planlist (
	  	  circle_acml_amt
	  	 , goal_prd
	  	 , aply_rate
	  	 , interest_ta
	  	 , wrt_dt
	  	 , tot_dpst_amt
	  	 , tot_dpst_int
	  	 , int_tax_amt
	  	 , atx_rcve_amt
	  	 , cust_sn
	  	 , prod_no
	  	 , prod_type
	  	 )
	values (
	  	  #{circle_acml_amt}
	  	 , #{goal_prd}
	  	 , cast(#{aply_rate} as numeric(5,2))
	  	 , #{interest_ta}
	  	 , now()
	  	 , #{tot_dpst_amt}
	  	 , #{tot_dpst_int}
	  	 , #{int_tax_amt}
	  	 , #{atx_rcve_amt}
	  	 , cast(#{cust_sn} as numeric(5))
	  	 , #{prod_no}
	  	 , #{prod_type}
	     )

mapper.update:
/* system.team2.promion_mng.updateDsgnSavgpl */
	update kcgbd.e_planlist
	   set circle_acml_amt = #{circle_acml_amt}
	  	 , goal_prd = #{goal_prd}
	  	 , aply_rate = #{aply_rate}
	  	 , interest_ta = #{interest_ta}
	  	 , wrt_dt = TO_DATE(#{wrt_dt}, 'yyyy-mm-dd')
	  	 , tot_dpst_amt = #{tot_dpst_amt}
	  	 , tot_dpst_int = #{tot_dpst_int}
	  	 , int_tax_amt = #{int_tax_amt}
	  	 , atx_rcve_amt = #{atx_rcve_amt}
	  	 , cust_sn = cast(#{cust_sn} as numeric(5))
	  	 , prod_no = #{prod_no}
	  	 , prod_type = #{prod_type}
	 where plan_no = cast(#{plan_no} as numeric(5))

